<script>
$(document).ready(function() {
    var $slider = $('.tbd-funnel-page');
    var $progressBar = $('.progress');
    var $progressBarLabel = $('.slider__label');

    $slider.on('beforeChange', function(event, slick, currentSlide, nextSlide) {
        var calc = ((nextSlide) / (slick.slideCount - 1)) * 100;

        $progressBar
            .css('background-size', calc + '% 100%')
            .attr('aria-valuenow', calc);

        $progressBarLabel.text(calc + '% completed');
    });

    $slider.slick({
        slidesToShow: 1,
        infinite: false,
        nextArrow: $("#scrollnextclass"),
        prevArrow: $('#scrollprevclass'),
        slidesToScroll: 1,
        draggable: false,
        speed: 400
    });

    $('.close-tab').click(function() {
        var slider = $(".tbd-funnel-page");
        slider.slick("slickGoTo", 0);
    });

    $('.funnel-toc-inner-pages a').click(function() {
        var idd = $(this).attr('idd')
        console.log(idd)
        var slider = $(".tbd-funnel-page");
        slider.slick("slickGoTo", idd);
    });
    /* for shop */
    $('.pop-btn-lf a').click(function() {
        var idd = $(this).attr('id')
        console.log(idd)
        var slider = $(".tbd-funnel-page");
        slider.slick("slickGoTo", idd);
    });

    setTimeout(function() {

        $('#content').css('opacity', '1');


    }, 1000);


    $('.tbd-funnel-page')
        .on('beforeChange', function() {
            setTimeout(function() {
                $('.right-inner').removeClass("aos-animate");
                $('.left-sec').removeClass("aos-animate");
                $('.hero-logo').removeClass("aos-animate");
                $('.sap-lg-heading').removeClass("aos-animate");
            }, 50);

        })
        .on('afterChange', function(event, slick, currentSlide) {
            setTimeout(function() {
                $('.slick-active .right-inner').addClass("aos-animate");
                $('.slick-active .left-sec').addClass("aos-animate");

                $('.hero-logo').addClass("aos-animate");
                $('.sap-lg-heading').addClass("aos-animate");

            }, 50);
        });


    setTimeout(function() {
        $('.hero-logo').removeClass("aos-animate");
        $('.sap-lg-heading').removeClass("aos-animate");

    }, 50);

    setTimeout(function() {
        $('.hero-logo').addClass("aos-animate");
        $('.sap-lg-heading').addClass("aos-animate");

    }, 1500);


});
</script>



